import React from 'react';
import { GoogleMap, Marker, InfoWindow, useLoadScript } from '@react-google-maps/api';
import { STYLE_CONTAI, MAPA_OPTION, MAPA_CENTER, KEY_GOOGLE } from "./mapConfig";

import Loading  from '../Loading';
import Error500 from '../Errors/500';

const Index = (props) => {
    const moviles = props.moviles;


    const [map, setMap] = React.useState(null);
    const [markers, setMarkers] = React.useState([]);
    const [selected, setSelected] = React.useState(null);

    const { isLoaded, loadError } = useLoadScript({
        // googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
        googleMapsApiKey: KEY_GOOGLE
    });

    const mapRef = React.useRef();
    const onMapLoad = React.useCallback((map) => {
        mapRef.current = map;
    }, []);

    const panTo = React.useCallback(({ lat, lng }) => {
        mapRef.current.panTo({ lat, lng });
        mapRef.current.setZoom(14);
    }, []);

    const onUnmount = React.useCallback(function callback(map) {
        setMap(null);
    }, []);


//   const KmlLayer = (map) => {
//     const rutas = [
//       'http://latinsoft.cl/archivos/circuitos/antofagasta/linea104/RUTA_104_IDA.kml', 
//       'http://latinsoft.cl/archivos/circuitos/antofagasta/linea104/RUTA_104_REG.kml'
//     ];

//     const listadoKml = [];
//     rutas.map( ruta => {
//       const kml = new google.maps.KmlLayer( {map: map, url: ruta, suppressInfoWindows: true} );
//       listadoKml.push(kml); 
//      });
//      listadoKml.map( kml => kml.setMap(map) );
//   }

    if (loadError) return <Error500 />;
    if (!isLoaded) return <Loading />;

    return (
        <div className="boxMapa">
            <GoogleMap 
                zoom={8} 
                onLoad={onMapLoad}
                onUnmount={onUnmount} 
                mapContainerStyle={ STYLE_CONTAI } 
                center={ MAPA_CENTER } 
                options={ MAPA_OPTION }
            >
            { /* Child components, such as markers, info windows, etc. */ }
            <></>
            </GoogleMap>
        </div>
    )
}

export default React.memo(Index);
